templates.template:
'''
Created on ${date}

@author: ${user}
'''

from jythonfx import fix
fix.getJavaFX()
from jythonfx.application import Application
from javafx.scene import *

class ${MyClass}(Application):

    def start(self, stage):
        ${cursor}
        self.title = "${MyClass}"
        self.pane = layout.AnchorPane()

        self.width = 300
        self.height = 250

        stage.setTitle(self.title)

        stage.setScene(Scene(self.pane, self.width, self.height))

        stage.show()

if __name__ == "__main__":
    Application.launch(${MyClass})
